package com.yishan.javaplus.config;import com.amazonaws.services.s3.AmazonS3;import com.yishan.javaplus.service.StorageService;import org.mockito.Mockito;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Primary;import java.io.IOException;@Configurationpublic class MockConfig {    @Bean    @Primary    public StorageService getStorageService() throws IOException {        AmazonS3 s3Client = Mockito.mock(AmazonS3.class);        StorageService storageService = new StorageService(s3Client);        storageService.setBucket("unit_test");        return storageService;    }}